import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

export default function CreatePostModal({ show, handleClose }) {
  const [data, setData] = useState({ content: '' });
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/api/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error while creating post ${response.status}`);
        }
        // Clear input fields on successful submission
        setData({ content: '' });
        setError('');
        handleClose(); // Close the modal after successful submission
      })
      .catch((error) => {
        console.error("Error creating post:", error);
        setError(error.message || "An error occurred");
      });
  };

  return (
    <div className='bg-black'>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Create Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            <div className="d-flex">
              <img
                src="https://github.com/mdo.png"
                alt="User"
                className="rounded-circle me-3"
                width={50}
                height={50}
              />
              <textarea
                className="form-control border-0"
                rows="4"
                placeholder="What's happening?"
                style={{ resize: 'none' }}
                value={data.content}
                onChange={(e) =>
                  setData({ ...data, content: e.target.value })
                }
                required
              ></textarea>
            </div>
            {/* Display error message in black color */}
            {error && <p style={{ color: "black" }}>{error}</p>}
            <Modal.Footer className="d-flex justify-content-between">
              <div>
                <i className="bi bi-image mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-camera mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-bar-chart mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-emoji-smile mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-calendar mx-2" style={{ cursor: 'pointer' }}></i>
              </div>
              <Button type="submit" variant="primary">
                Tweet
              </Button>
            </Modal.Footer>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
}
