import React, { useState, useEffect } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function Home() {
  const [tweets, setTweets] = useState([]);
  const [newPostText, setNewPostText] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setTweets(data); // Assuming data is an array of tweets with createdAt, postText, and media (Blob)
    } catch (error) {
      console.error('Error fetching tweets:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ postText: newPostText }),
      });

      if (!response.ok) {
        throw new Error(`Error while creating post ${response.status}`);
      }

      // Clear input fields on successful submission
      setNewPostText('');
      setError('');
      fetchTweets(); // Fetch updated tweets after posting
    } catch (error) {
      console.error('Error creating post:', error);
      setError(error.message || 'An error occurred');
    }
  };

  const renderMedia = (blob) => {
 
      const url = URL.createObjectURL(blob);
      return <img src={url} alt="Tweet Media" className="img-fluid rounded mb-3" />;

  };

  return (
    <div className="container-fluid g-0 bg-black">
      {/* Create Post Form */}
      <div className="card mb-3 bg-black text-white  rounded-0 " style={{border:" 1px solid #746E6D "}}>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="d-flex">
              <img
                src="https://github.com/mdo.png"
                alt="User"
                className="rounded-circle me-3"
                width={50}
                height={50}
              />
              <textarea
                className="form-control border-0 bg-secondary text-white"
                rows="4"
                placeholder="What's happening?"
                style={{ resize: 'none' }}
                value={newPostText}
                onChange={(e) => setNewPostText(e.target.value)}
                required
              ></textarea>
            </div>
            {/* Display error message in white color */}
            {error && <p style={{ color: 'white' }}>{error}</p>}
            <div className="d-flex justify-content-between border-top border-secondary mt-3">
              <div>
                <i className="bi bi-image mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-camera mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-bar-chart mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-emoji-smile mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-calendar mx-2" style={{ cursor: 'pointer' }}></i>
              </div>
              <button type="submit" className="btn btn-primary">
                Tweet
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Display Tweets */}
      <div className="row justify-content-center">
        <div className="col-md-12">
          {tweets.map(tweet => (
            <div key={tweet.id} className="card  rounded-0 bg-black text-white  " style={{border:" 1px solid #746E6D "}}>
              <div className="card-body">
                <div className="d-flex align-items-center mb-3">
                  <img
                    src="https://github.com/mdo.png" // Replace with actual avatar URL if available in tweet data
                    alt="Avatar"
                    className="rounded-circle me-3"
                    width={50}
                    height={50}
                  />
                  <div>
                    <h5 className="card-title mb-0">Username</h5> {/* Replace with actual username if available in tweet data */}
                    <span className="text-white">@handle</span> {/* Replace with actual handle if available in tweet data */}
                  </div>
                </div>
                <p className="card-text">{tweet.postText}</p> {/* Display tweet text */}
                {tweet.media && renderMedia(tweet.media)}
                <div className="text-gray">{tweet.createdAt}</div> {/* Display tweet creation date */}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
