import React, { useState, useEffect } from 'react';

const UserSearch = ({ show, onHide, onUserSelect }) => {
    const [query, setQuery] = useState('');
    const [users, setUsers] = useState([]);

    useEffect(() => {
        if (query.length > 2) {
            fetch(`https://twit-backend-production.up.railway.app/api/posts/searchuser?q=${query}`, {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            })
                .then(response => response.json())
                .then(data => setUsers(data))
                .catch(error => console.error('Error fetching users:', error));
        } else {
            setUsers([]);
        }
    }, [query]);

    return (
        <div className={`modal ${show ? 'show' : ''}`} style={{ display: show ? 'block' : 'none' }}>
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Search Users</h5>
                        <button type="button" className="btn-close" onClick={onHide}></button>
                    </div>
                    <div className="modal-body">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search by username..."
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                        />
                        <ul className="list-group mt-3">
                            {users.map(user => (
                                <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
                                    <div>
                                        <img
                                            src={user.profilePic}
                                            alt={user.name}
                                            className="rounded-circle me-2"
                                            style={{ width: '30px', height: '30px' }}
                                        />
                                        {user.name}
                                    </div>
                                    <button className="btn btn-primary btn-sm" onClick={() => onUserSelect(user.id)}>
                                        Chat
                                    </button>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserSearch;
