import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

export default function Explore() {
  const location = useLocation();
  const searchQuery = location.state?.searchQuery || '';

  const [users, setUsers] = useState([]);
  const [tweets, setTweets] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSearchResults = async () => {
      setError(null);
      try {
        const response = await fetch(`http://localhost:5000/api/posts/search?q=${searchQuery}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `${localStorage.getItem('token')}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUsers(data.users || []);
          setTweets(data.tweets || []);
        } else {
          const errorText = await response.text();
          console.error('Error submitting search query:', errorText);
          setError('An error occurred while fetching the search results.');
          setUsers([]);
          setTweets([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setError('An unexpected error occurred.');
        setUsers([]);
        setTweets([]);
      }
    };

    if (searchQuery) {
      fetchSearchResults();
    }
  }, [searchQuery]);

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <h3>Search Results:</h3>
          <div className="results">
            {error && <p className="text-danger">{error}</p>}
            {users.length > 0 && (
              <>
                <h4>Users:</h4>
                {users.map((user) => (
                  <div key={user.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5>{user.name}</h5>
                    <p className="text-secondary">@{user.email}</p>
                  </div>
                ))}
              </>
            )}
            {tweets.length > 0 && (
              <>
                <h4>Tweets:</h4>
                {tweets.map((tweet) => (
                  <div key={tweet.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5 className="fw-light">{tweet.postText}</h5>
                    {tweet.media && (
                      <img
                        src={`http://localhost:5000/public/${tweet.media}`}
                        className="img-fluid rounded mt-2"
                        alt="Tweet Media"
                      />
                    )}
                  </div>
                ))}
              </>
            )}
            {users.length === 0 && tweets.length === 0 && (
              <p>No results found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
