import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

export default function Explore() {
  const location = useLocation();
  const initialSearchQuery = location.state?.searchQuery || '';

  const [searchQuery, setSearchQuery] = useState(initialSearchQuery);
  const [userFetch, setUserFetch] = useState([]); // Changed variable name
  const [tweets, setTweets] = useState([]); // Added for tweets
  const [error, setError] = useState(null);

  useEffect(() => {
    if (searchQuery) {
      fetchSearchResults(searchQuery); // Adjusted function name
    }
  }, [searchQuery]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);

    // Fetch users based on search input
    const fetchUsers = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/posts/search?q=${e.target.value}`, {
                headers: { Authorization: `${localStorage.getItem('token')}` },
            });

            // Ensure the response is an array of users
            const data = response.data;
            setFilteredUsers(Array.isArray(data) ? data : []); // Ensure it's an array or empty
        } catch (error) {
            console.error('Error searching users:', error);
            setFilteredUsers([]); // Reset to an empty array in case of error
        }
    };

    fetchUsers();
};


  const fetchUsers = async (tweets) => {
    const userIds = [...new Set(tweets.map(tweet => tweet.userId))];
    const userPromises = userIds.map(async (userId) => {
      try {
        const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
          method: 'GET',
          headers: {
            'Authorization': `${localStorage.getItem('token')}`,
          },
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          return { [userId]: userData };
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
      return null;
    });

    const usersData = await Promise.all(userPromises);
    const usersMap = usersData.reduce((acc, user) => ({ ...acc, ...user }), {});
    setUserFetch(usersMap); // Set users
  };

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    fetchSearchResults(searchQuery); // Adjusted function name
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSearchSubmit} className="d-flex">
            <input
              type="text"
              placeholder="Search"
              value={searchQuery}
              onChange={handleInputChange}
              className="px-4 py-2 my-2 rounded-pill border-primary bg-black text-white flex-grow-1"
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-2 d-flex align-items-center">
              <i className="bi bi-search"></i>
            </button>
          </form>
          <h3>Search Results:</h3>
          <div className="results">
            {error && <p className="text-danger">{error}</p>}
            {userFetch.length > 0 && (
              <>
                <h4>Users:</h4>
                {userFetch.map((user) => (
                  <div key={user.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5>{user.name}</h5>
                    <p className="text-secondary">@{user.email}</p>
                  </div>
                ))}
              </>
            )}
            {tweets.length > 0 && (
              <>
                <h4>Tweets:</h4>
                {tweets.map((tweet) => (
                  <div key={tweet.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    {userFetch[tweet.userId] && (
                      <div>
                        <h5>{userFetch[tweet.userId].name}</h5>
                        <p className="text-secondary">@{userFetch[tweet.userId].email}</p>
                      </div>
                    )}
                    <h5 className="fw-light">{tweet.postText}</h5>
                    {tweet.media && (
                      <img
                        src={`http://localhost:5000/public/${tweet.media}`}
                        className="img-fluid rounded mt-2"
                        alt="Tweet Media"
                      />
                    )}
                  </div>
                ))}
              </>
            )}
            {tweets.length === 0 && (
              <p>No tweets found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
