// components/UserSearch.js
import React, { useState, useEffect } from 'react';

const UserSearch = ({ show, onHide, onUserSelect }) => {
    const [users, setUsers] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        if (search) {
            fetch(`https://twit-backend-production.up.railway.app/api/posts/searchuser?query=${search}`)
                .then(response => response.json())
                .then(data => setUsers(data))
                .catch(error => console.error('Error fetching users:', error));
        }
    }, [search]);

    return (
        <div className={`modal ${show ? 'show' : ''}`} style={{ display: show ? 'block' : 'none' }}>
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Search Users</h5>
                        <button type="button" className="btn-close" onClick={onHide}></button>
                    </div>
                    <div className="modal-body">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search users..."
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                        />
                        <ul className="list-group mt-2">
                            {users.map(user => (
                                <li key={user.id} className="list-group-item d-flex align-items-center">
                                    <img
                                        src={`${user.profilePic}`}
                                        alt={`${user.name}'s profile`}
                                        className="rounded-circle me-2"
                                        style={{ width: '40px', height: '40px' }}
                                    />
                                    {user.name}
                                    <button
                                        className="btn btn-primary btn-sm ms-auto"
                                        onClick={() => onUserSelect(user.id)}
                                    >
                                        Chat
                                    </button>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserSearch;
