import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io("http://localhost:5000"); // Adjust based on your backend's address

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [receiverId, setReceiverId] = useState(null); // This should be set based on the user selection
  const senderId = localStorage.getItem('userId'); // Assume senderId is stored in local storage

  useEffect(() => {
    if (receiverId) {
      // Fetch chat history
      fetch(`/api/chat/history/${senderId}/${receiverId}`)
        .then(response => response.json())
        .then(data => {
          setMessages(data);
        })
        .catch(error => {
          console.error('Error fetching chat history:', error);
        });

      // Listen for new messages
      socket.on('receiveMessage', (newMessage) => {
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      });
    }

    return () => {
      socket.off('receiveMessage');
    };
  }, [receiverId]);

  const sendMessage = () => {
    const newMessage = { senderId, receiverId, content: message };
    socket.emit('sendMessage', newMessage);

    fetch('/api/chat/send', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newMessage)
    })
      .then(response => response.json())
      .then(data => {
        setMessages([...messages, data]);
        setMessage('');
      })
      .catch(error => {
        console.error('Error sending message:', error);
      });
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.senderId === senderId ? 'sent' : 'received'}`}>
            {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}
