import React, { useState } from 'react';

export default function Thirdbar() {
  const [searchQuery, setSearchQuery] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error state before making the request
    try {
      const response = await fetch(`http://localhost:5000/api/posts/search?q=${searchQuery}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        // Log data for debugging
        console.log('Search results:', data);
        setResults(data.results || []); // Ensure results is set to an empty array if data.results is undefined
      } else {
        const errorText = await response.text();
        console.error('Error submitting search query:', errorText);
        setError('An error occurred while fetching the search results.');
        setResults([]); // Clear results on error
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
      setResults([]); // Clear results on error
    }
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Search"
              className="input-group mx-2 my-4 rounded-pill p-2 bg-dark text-white border-primary"
              value={searchQuery}
              onChange={handleInputChange}
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-4">Search</button>
          </form>
          {error && <p className="text-danger">{error}</p>} {/* Display error message if there's an error */}
        </div>
        <div className="col-12">
          <h3>Search Results:</h3>
          <div className="results">
            {results.length > 0 ? (
              results.map((item, index) => (
                <div key={index} className="result-item">
                  {/* Modify this part to display user or tweet information */}
                  <p>{item.title || item.username || 'No title or username'}</p>
                </div>
              ))
            ) : (
              <p>No results found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
