import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Tweets = () => {
  const [tweets, setTweets] = useState([]);
  const [users, setUsers] = useState({});
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'GET',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Error fetching tweets: ${response.status}`);
      }

      const data = await response.json();
      setTweets(data);
      fetchUsers(data);
    } catch (error) {
      console.error('Error fetching tweets:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (tweets) => {
    const userIds = [...new Set(tweets.map(tweet => tweet.userId))];
    userIds.forEach(async (userId) => {
      if (!users[userId]) {
        try {
          const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
            method: 'GET',
            headers: {
              'Authorization': `${localStorage.getItem('token')}`,
            },
          });

          if (userResponse.ok) {
            const userData = await userResponse.json();
            setUsers((prevUsers) => ({ ...prevUsers, [userId]: userData }));
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      }
    });
  };

  useEffect(() => {
    fetchTweets();
  }, []);

  if (loading) {
    return <div className="container"><div className="alert alert-info">Loading...</div></div>;
  }

  return (
    <div className="container ">
      {error && <div className="alert alert-danger">Error: {error}</div>}
      <ul className="">
        {tweets.map((tweet) => (
          <li key={tweet.id} className=" border border-secondary bg-dark text-white list-unstyled">
            <div className="media">
              <div className="media-body">
                {users[tweet.userId] && (
                  <h5 className="">
                    {users[tweet.userId].name}{' '}
                    <small className="text-secondary">@{users[tweet.userId].email}</small>
                  </h5>
                )}
                <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
                <p className="">{tweet.postText}</p>
                <div className="container-fluid g-0">
                <div className="row">
                  <div className="col-12">
                    
                  </div>
                </div>
                </div>
                {tweet.media && (
                  <img
                    src={`http://localhost:5000/public/${tweet.media}`}
                    className="img-fluid rounded "
                    alt="Tweet Media"
                  />
                )}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tweets;
