import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5001'); // Make sure this URL matches your backend

const Chat = ({ userId, otherUserId }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Join the chat room for the selected user pair
    socket.emit('joinRoom', { userId, otherUserId });

    // Listen for incoming messages
    socket.on('receiveMessage', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Clean up on component unmount
    return () => {
      socket.off('receiveMessage');
      socket.emit('leaveRoom', { userId, otherUserId });
    };
  }, [userId, otherUserId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      const data = {
        senderId: userId,
        receiverId: otherUserId,
        content: message,
      };
      socket.emit('sendMessage', data);
      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <div className="messages" style={{ height: '300px', overflowY: 'scroll' }}>
        <ul className="list-unstyled">
          {messages.map((msg, index) => (
            <li key={index} className={msg.senderId === userId ? 'my-message' : 'their-message'}>
              <span>{msg.content}</span>
            </li>
          ))}
        </ul>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          />
          <button type="submit" className="btn btn-primary">
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
