import React, { useState, useEffect } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function Home() {
  const [tweets, setTweets] = useState([]);
  const [newPostText, setNewPostText] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setTweets(data);
    } catch (error) {
      console.error('Error fetching tweets:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ postText: newPostText }),
      });

      if (!response.ok) {
        throw new Error(`Error while creating post ${response.status}`);
      }

      setNewPostText('');
      setError('');
      fetchTweets();
    } catch (error) {
      console.error('Error creating post:', error);
      setError(error.message || 'An error occurred');
    }
  };

  const renderMedia = (blob) => {
    if (blob instanceof Blob) {
      const url = URL.createObjectURL(blob);
      return <img src={url} alt="Tweet Media" className="img-fluid rounded mb-3" />;
    }
    return null;
  };

  return (
    <div className="container-fluid g-0 bg-black">
      <div className="card mb-3 bg-black text-white rounded-0">
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="d-flex">
              <img
                src="https://github.com/mdo
                .png"
                alt="User"
                width={50}
                height={50}
                className="rounded-circle me-2"
              />
              <textarea
                className="form-control bg-black text-white border-0"
                placeholder="What's happening?"
                value={newPostText}
                onChange={(e) => setNewPostText(e.target.value)}
                rows="3"
              ></textarea>
            </div>
            {error && <div className="text-danger mt-2">{error}</div>}
            <div className="d-flex justify-content-between align-items-center mt-2">
              <div>
                <button type="button" className="btn btn-outline-primary btn-sm rounded-pill me-2">
                  <i className="bi bi-image"></i>
                </button>
                <button type="button" className="btn btn-outline-primary btn-sm rounded-pill">
                  <i className="bi bi-file-earmark-play"></i>
                </button>
              </div>
              <button type="submit" className="btn btn-primary rounded-pill">Tweet</button>
            </div>
          </form>
        </div>
      </div>

      <div className="tweet-list">
        {tweets.map((tweet) => (
          <div key={tweet.id} className="card mb-3 bg-dark text-white rounded-0">
            <div className="card-body">
              <div className="d-flex">
                <img
                  src="https://github.com/mdo.png"
                  alt="User"
                  width={50}
                  height={50}
                  className="rounded-circle me-2"
                />
                <div>
                  <h5 className="card-title">@{tweet.username}</h5>
                  <p className="card-text">{tweet.postText}</p>
                  {renderMedia(tweet.media)}
                  <p className="card-text">
                    <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
                  </p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
