import React, { useState, useEffect } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';
import CreatePostModal from '../Components/Post';
export default function Home() {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setTweets(data); // Assuming data is an array of tweets with createdAt, postText, and media (Blob)
    } catch (error) {
      console.error('Error fetching tweets:', error);
    }
  };

  const renderMedia = (blob) => {
    if (blob instanceof Blob) {
      const url = URL.createObjectURL(blob);
      return <img src={url} alt="Tweet Media" className="img-fluid rounded mb-3" />;
    }
    return null;
  };

  return (
<>

<CreatePostModal show={showModal} handleClose={handleClose}></CreatePostModal>
{tweets.map(tweet => (
  <div key={tweet.id} className="card mb-3 bg-black text-white rounded border border-gray">
    <div className="card-body">
      <div className="d-flex align-items-center mb-3">
        <img
          src="https://github.com/mdo.png" // Replace with actual avatar URL if available in tweet data
          alt="Avatar"
          className="rounded-circle me-3"
          width={50}
          height={50}
        />
        <div>
          <h5 className="card-title mb-0">Username</h5> {/* Replace with actual username if available in tweet data */}
          <span className="text-white">@handle</span> {/* Replace with actual handle if available in tweet data */}
        </div>
      </div>
      <p className="card-text">{tweet.postText}</p> {/* Display tweet text */}
      {tweet.media && renderMedia(tweet.media)}
      <div className="text-gray">{tweet.createdAt}</div> {/* Display tweet creation date */}
    </div>
  </div>
))}

</>
   
  );
}
