import { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5000');

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [users, setUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState(''); // State for search query
    const [otherUserId, setOtherUserId] = useState(null);
    const userToken = localStorage.getItem('token');
    const userId = userToken ? parseJwt(userToken).id : null;
    const roomId = otherUserId ? generateRoomId(userId, otherUserId) : null;

    useEffect(() => {
        if (!userId) return;

        const fetchUsers = async (query = '') => {
            try {
                const response = await axios.get('http://localhost:5000/api/posts/searchuser', {
                    params: { q: query }, // Pass the query as a parameter
                    headers: { 'Authorization': ` ${localStorage.getItem("token")}` }
                });
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers(searchQuery); // Fetch users with current search query

        if (roomId) {
            socket.emit('joinRoom', { roomId, userId });
            socket.on('message', (message) => {
                setMessages((prevMessages) => [...prevMessages, message]);
            });

            return () => {
                socket.off('message');
            };
        }
    }, [roomId, userId, otherUserId, searchQuery]); // Add searchQuery to dependency array

    const sendMessage = () => {
        if (message.trim() === '' || !roomId) return;

        socket.emit('chatMessage', { roomId, message });
        setMessage('');
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    return (
        <div>
            <div>
                <h3>Search Users</h3>
                <input
                    type="text"
                    placeholder="Search users"
                    value={searchQuery}
                    onChange={handleSearchChange}
                    className="px-4 py-2 my-2 rounded-pill border-primary bg-black text-white"
                />
                <ul>
                    {users.map((user) => (
                        <li key={user.id} onClick={() => setOtherUserId(user.id)}>
                            {user.name}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h3>Chat</h3>
                <div>
                    {messages.map((msg, index) => (
                        <div key={index}>{msg}</div>
                    ))}
                </div>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
};

// Helper function to decode JWT token
function parseJwt(token) {
    if (!token) return {};
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
    return JSON.parse(jsonPayload);
}

function generateRoomId(userId1, userId2) {
    const sortedIds = [userId1, userId2].sort();
    return `${sortedIds[0]}_${sortedIds[1]}`;
}

export default Chat;
