import React, { useState, useEffect } from 'react';

const ChatWithUserSelect = ({ userId }) => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5001/api/users/all') // Adjust URL as necessary
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleUserSelect = (e) => {
    const selectedUserId = e.target.value;
    setSelectedUserId(selectedUserId);
  };

  return (
    <div>
      <div className="form-group">
        <label htmlFor="userSelect">Select User to Chat With:</label>
        <select className="form-control" id="userSelect" onChange={handleUserSelect}>
          <option value="">--Select User--</option>
          {users.map(user => (
            <option key={user.id} value={user.id}>{user.name}</option>
          ))}
        </select>
      </div>

      {selectedUserId ? (
        <Chat userId={userId} otherUserId={selectedUserId} />
      ) : (
        <p>Please select a user to start chatting.</p>
      )}
    </div>
  );
};

export default ChatWithUserSelect;
