import React, { useState, useEffect } from 'react';

const UserSelect = ({ onSelectUser }) => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/users/all') // Adjust URL as necessary
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleSelect = (e) => {
    const selectedUserId = e.target.value;
    onSelectUser(selectedUserId);
  };

  return (
    <div className="form-group">
      <label htmlFor="userSelect">Select User to Chat With:</label>
      <select className="form-control" id="userSelect" onChange={handleSelect}>
        <option value="">--Select User--</option>
        {users.map(user => (
          <option key={user.id} value={user.id}>{user.name}</option>
        ))}
      </select>
    </div>
  );
};

export default UserSelect;
