import React from 'react';
import './App.css';
import Sidebar from './Components/sidebar';
import Home from './Pages/Home';
import Explore from './Pages/explore';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Login from './Pages/login';
import Signup from './Pages/signup';
import ProtectedRoute from './Pages/protectedRoute';
import IndexPage from './Pages/IndexPage';
import CreatePostModal from './Components/Post';
import { useState } from 'react';

function App() {
  const token = localStorage.getItem('token');
  const location = useLocation();
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);
  return (
    <>
      <div className="container-fluid">
        <div className="row">
          {token && (
            <div className="col-auto col-md-3 col-xl-3 col-sm-2 px-0 bg-black">
              <Sidebar />
            </div>
          )}
          <div className={`col ${token ? 'col-md-6 col-xl-6 col-sm-10 px-0' : ''}`}>
            <Routes>
              {token ? (
                <>
                  <Route path="/home" element={<ProtectedRoute><Home /></ProtectedRoute>} />
                  <Route path="/explore" element={<ProtectedRoute><Explore /></ProtectedRoute>} />
                  <Route path="*" element={<Navigate to="/home" />} />
                </>
              ) : (
                <>
                  <Route path="/login" element={  <Login show={showModal} handleClose={handleClose} />} />
                  <Route path="/signup" element={<Signup />} />
                  <Route path="/" element={<IndexPage />} />
                  <Route path="*" element={<Navigate to="/login" />} />

                </>
              )}
            </Routes>
          </div>
        </div>
      </div>

      {/* Conditionally render the modal based on the current location */}
      {token && location.pathname === '/post' && <CreatePostModal />}
    </>
  );
}

export default App;
