import React, { useState, useEffect } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function Home() {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setTweets(data); // Assuming data is an array of tweets
    } catch (error) {
      console.error('Error fetching tweets:', error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8">
          {tweets.map(tweet => (
            <div key={tweet.id} className="card mb-3">
              <div className="card-body">
                <div className="d-flex align-items-center mb-3">
                  <img
                    src={tweet.author.avatar} // Assuming tweet.author.avatar is the URL for author's avatar
                    alt="Avatar"
                    className="rounded-circle me-3"
                    width={50}
                    height={50}
                  />
                  <h5 className="card-title mb-0">{tweet.author.username}</h5> {/* Username of the author */}
                  <span className="text-muted ms-2">@{tweet.author.handle}</span> {/* Handle of the author */}
                </div>
                <p className="card-text">{tweet.text}</p> {/* Content of the tweet */}
                {tweet.media && (
                  <img
                    src={tweet.media} // Assuming tweet.media is the URL for media (image or video)
                    alt="Tweet Media"
                    className="img-fluid rounded mb-3"
                  />
                )}
                <div className="d-flex justify-content-between">
                  <div>
                    <i className="bi bi-chat"></i> {tweet.comments.length}
                  </div>
                  <div>
                    <i className="bi bi-heart"></i> {tweet.likes}
                  </div>
                  <div>
                    <i className="bi bi-share"></i> {tweet.shares}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
