import { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [users, setUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [otherUserId, setOtherUserId] = useState(null);
    const userToken = localStorage.getItem('token');
    const userId = userToken ? parseJwt(userToken).id : null;
    const roomId = otherUserId ? generateRoomId(userId, otherUserId) : null;

    useEffect(() => {
        if (!userId) return;

        const fetchUsers = async (query = '') => {
            try {
                const response = await fetch(`http://localhost:5000/api/posts/searchuser?q=${query}`, {
                    headers: { 'Authorization': ` ${localStorage.getItem("token")}` }
                });
                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers(searchQuery);

        if (roomId) {
            socket.emit('joinRoom', { roomId, userId });
            socket.on('message', (message) => {
                setMessages((prevMessages) => [...prevMessages, message]);
            });

            return () => {
                socket.off('message');
            };
        }
    }, [roomId, userId, otherUserId, searchQuery]);

    const sendMessage = () => {
        if (message.trim() === '' || !roomId) return;

        socket.emit('chatMessage', { roomId, message });
        setMessage('');
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleUserSelect = (id) => {
        setOtherUserId(id);
    };

    return (
        <div className="container mt-4">
            <div className="row">
                {/* Search User Section */}
                <div className={`col-md-4 ${otherUserId ? 'd-none d-md-block' : ''}`}>
                    <h3>Search Users</h3>
                    <div className="input-group mb-3">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search users"
                            value={searchQuery}
                            onChange={handleSearchChange}
                        />
                    </div>
                    <ul className="list-group">
                        {users.map((user) => (
                            <li
                                key={user.id}
                                className="list-group-item d-flex justify-content-between align-items-center cursor-pointer"
                                onClick={() => handleUserSelect(user.id)}
                            >
                                <img
                                    src={`http://localhost:5000/public/${user.profilePic}`}
                                    alt={`${user.name}'s profile`}
                                    className="rounded-circle me-2"
                                    style={{ width: '40px', height: '40px' }}
                                />
                                {user.name}
                            </li>
                        ))}
                    </ul>
                    {/* Only show this button on small screens */}
                    {otherUserId && (
                        <div className="d-md-none mt-3">
                            <button
                                className="btn btn-primary"
                                onClick={() => setOtherUserId(null)}
                            >
                                Back to Search
                            </button>
                        </div>
                    )}
                </div>

                {/* Chat Section */}
                <div className={`col-md-8 ${otherUserId ? 'd-block' : 'd-none d-md-block'}`}>
                    {otherUserId && (
                        <>
                            <h3>Chat with User</h3>
                            <div className="border rounded p-3 mb-3" style={{ height: '400px', overflowY: 'scroll' }}>
                                {messages.map((msg, index) => (
                                    <div key={index} className="mb-2">
                                        <div className="bg-light p-2 rounded">{msg}</div>
                                    </div>
                                ))}
                            </div>
                            <div className="input-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    value={message}
                                    onChange={(e) => setMessage(e.target.value)}
                                />
                                <button className="btn btn-primary" onClick={sendMessage}>
                                    Send
                                </button>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
};

// Helper function to decode JWT token
function parseJwt(token) {
    if (!token) return {};
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
    return JSON.parse(jsonPayload);
}

function generateRoomId(userId1, userId2) {
    const sortedIds = [userId1, userId2].sort();
    return `${sortedIds[0]}_${sortedIds[1]}`;
}

export default Chat;
