import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CreatePostModal({ show, handleClose }) {
  const [data, setData] = useState({ postText: '', media: null }); // media should be null initially
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token'); // Retrieve the token from local storage

    const formData = new FormData();
    formData.append('postText', data.postText);
    if (data.media) formData.append('media', data.media);

    fetch("http://localhost:5000/api/posts/", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${token}` // Include the token in the Authorization header
      },
      body: formData, // Use FormData to handle file uploads
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error while creating post ${response.status}`);
        }
        // Clear input fields on successful submission
        setData({ postText: '', media: null });
        setError('');
        handleClose(); // Close the modal after successful submission
      })
      .catch((error) => {
        console.error("Error creating post:", error);
        setError(error.message || "An error occurred");
      });
  };

  return (
    <div className='bg-black'>
      <Modal
        show={show}
        onHide={handleClose}
        centered
        contentClassName="bg-black text-white"
        dialogClassName="modal-top"
      >
        <Modal.Header closeButton className="border-bottom border-secondary">
          <Modal.Title>Create Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            <div className="d-flex">
              <img
                src="https://github.com/mdo.png"
                alt="User"
                className="rounded-circle me-3"
                width={50}
                height={50}
              />
              <textarea
                className="form-control border-0 bg-secondary text-white"
                rows="4"
                placeholder="What's happening?"
                style={{ resize: 'none' }}
                value={data.postText}
                onChange={(e) =>
                  setData({ ...data, postText: e.target.value })
                }
                required
              ></textarea>
            </div>
            <input 
              type="file"     
              onChange={(e) =>
                setData({ ...data, media: e.target.files[0] }) // Set file instead of file path
              }
            />
            {/* Display error message in white color */}
            {error && <p style={{ color: "white" }}>{error}</p>}
            <Modal.Footer className="d-flex justify-content-between border-top border-secondary">
              <div>
                <i className="bi bi-image mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-camera mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-bar-chart mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-emoji-smile mx-2" style={{ cursor: 'pointer' }}></i>
                <i className="bi bi-calendar mx-2" style={{ cursor: 'pointer' }}></i>
              </div>
              <Button type="submit" variant="primary">
                Tweet
              </Button>
            </Modal.Footer>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
}
