import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

export default function Explore() {
  const location = useLocation();
  const initialSearchQuery = location.state?.searchQuery || '';

  const [searchQuery, setSearchQuery] = useState(initialSearchQuery);
  const [user, setUser] = useState(null);
  const [tweet, setTweet] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (searchQuery) {
      fetchTweetById(searchQuery);
    }
  }, [searchQuery]);

  const fetchTweetById = async (tweetId) => {
    setError(null);
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${tweetId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const tweetData = await response.json();
        setTweet(tweetData);
        fetchUser(tweetData.userId);
      } else {
        const errorText = await response.text();
        console.error('Error fetching tweet:', errorText);
        setError('An error occurred while fetching the tweet.');
        setUser(null);
        setTweet(null);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
      setUser(null);
      setTweet(null);
    }
  };

  const fetchUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
        method: 'GET',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
      } else {
        console.error('Error fetching user data');
        setError('An error occurred while fetching the user data.');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred while fetching user data.');
    }
  };

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    fetchTweetById(searchQuery);
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSearchSubmit} className="d-flex">
            <input
              type="text"
              placeholder="Enter Tweet ID"
              value={searchQuery}
              onChange={handleInputChange}
              className="px-4 py-2 my-2 rounded-pill border-primary bg-black text-white flex-grow-1"
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-2 d-flex align-items-center">
              <i className="bi bi-search"></i>
            </button>
          </form>
          <h3>Search Results:</h3>
          <div className="results">
            {error && <p className="text-danger">{error}</p>}
            {user && tweet && (
              <div className="result-item border border-secondary bg-black text-white p-3 mb-3">
                <h5>{user.name}</h5>
                <p className="text-secondary">@{user.email}</p>
                <h5 className="fw-light">{tweet.postText}</h5>
                {tweet.media && (
                  <img
                    src={`http://localhost:5000/public/${tweet.media}`}
                    className="img-fluid rounded mt-2"
                    alt="Tweet Media"
                  />
                )}
              </div>
            )}
            {!user && !tweet && !error && <p>No results found</p>}
          </div>
        </div>
      </div>
    </div>
  );
}
