import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Thirdbar() {
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error state before making the request
    try {
      const response = await fetch(`http://localhost:5000/api/posts/search?q=${searchQuery}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Search results:', data); // Log data for debugging
        // Navigate to Explore with search results
        navigate('/explore', { state: { searchQuery, users: data.users || [], tweets: data.tweets || [] } });
      } else {
        const errorText = await response.text();
        console.error('Error submitting search query:', errorText);
        setError('An error occurred while fetching the search results.');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
    }
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Search"
              className="input-group mx-2 my-4 rounded-pill p-2 bg-dark text-white border-primary"
              value={searchQuery}
              onChange={handleInputChange}
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-4">Search</button>
          </form>
          {error && <p className="text-danger">{error}</p>} {/* Display error message if there's an error */}
        </div>
      </div>
    </div>
  );
}
