import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import UserSearch from './UserSearch';

const socket = io("https://twit-backend-production.up.railway.app" || 'http://localhost:5000', { perMessageDeflate: false });

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [showUserSearch, setShowUserSearch] = useState(false);
    const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

    const userToken = localStorage.getItem('token');
    const userId = userToken ? parseJwt(userToken).id : null;
    const roomId = selectedUser ? generateRoomId(userId, selectedUser.id) : null;

    useEffect(() => {
        const handleResize = () => setIsSmallScreen(window.innerWidth < 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        if (!userId || !roomId) return;

        console.log("Joining room:", roomId);
        socket.emit('joinRoom', { roomId, userId });

        const handleMessage = (message) => {
            setMessages(prevMessages => [...prevMessages, message]);
        };

        socket.on('message', handleMessage);

        // Listen for notification of new messages
        socket.on('notifyUser', ({ senderId, recipientId }) => {
            if (recipientId === userId) {
                console.log(`User ${senderId} wants to chat with you!`);
                // Handle notification UI here
            }
        });

        return () => {
            socket.off('message', handleMessage);
            socket.off('notifyUser');
        };
    }, [roomId, userId, selectedUser]);

    const sendMessage = () => {
        if (message.trim() === '' || !roomId) return;

        socket.emit('chatMessage', { roomId, message, senderId: userId, recipientId: selectedUser.id });
        setMessage('');
    };

    const handleUserSelect = async (userId) => {
        // Fetch user details
        await fetchSelectedUserDetails(userId);
        // Clear previous messages
        setMessages([]);
        // Hide the user search modal
        setShowUserSearch(false);
    };

    const fetchSelectedUserDetails = async (userId) => {
        try {
            const response = await fetch(`https://twit-backend-production.up.railway.app/api/posts/user/${userId}`, {
                headers: { 'Authorization': `${localStorage.getItem("token")}` }
            });
            const data = await response.json();
            setSelectedUser(data);
        } catch (error) {
            console.error('Error fetching selected user details:', error);
        }
    };

    const handleShowUserSearch = () => setShowUserSearch(true);
    const handleCloseUserSearch = () => setShowUserSearch(false);

    return (
        <div className="container-fluid g-0 bg-black">
            <div className="row" style={{ height: '100vh' }}>
                {/* User Search Section */}
                <div
                    className={`col-md-4 ${isSmallScreen && selectedUser ? 'd-none' : ''}`}
                    style={{ borderRight: '2px solid grey', borderLeft: '2px solid grey' }}
                >
                    <div className="fs-3 fw-bold m-2 text-white">Messages</div>
                    <div className="fs-2 fw-bold m-2 my-4 text-white">Welcome to your <br /> inbox!</div>
                    <div className="fw-light m-2 my-1 text-secondary" style={{ fontSize: "16px" }}>
                        Drop a line, share posts and more with private conversations between you and others on X.
                    </div>

                    <hr />
                    <button className="btn btn-primary mt-3" onClick={handleShowUserSearch}>
                        Search Users
                    </button>
                    <UserSearch
                        show={showUserSearch}
                        onHide={handleCloseUserSearch}
                        onUserSelect={handleUserSelect}
                    />
                </div>

                {/* Chat Section */}
                <div
                    className={`col-md-8 ${selectedUser ? 'd-block' : 'd-none d-md-block'}`}
                    style={{ borderRight: '2px solid grey', borderLeft: '2px solid grey' }}
                >
                    {selectedUser ? (
                        <>
                            <div className="d-flex align-items-center p-3 border-bottom">
                                <img
                                    src={`https://twit-backend-production.up.railway.app/public/${selectedUser.profilePic}`}
                                    alt={`${selectedUser.name}'s profile`}
                                    className="rounded-circle me-2"
                                    style={{ width: '40px', height: '40px' }}
                                />
                                <h5 className="m-0 text-white fw-bold">{selectedUser.name}</h5>
                            </div>
                            <div className="border rounded p-3 mb-3" style={{ height: '400px', overflowY: 'scroll' }}>
                                {messages.map((msg, index) => (
                                    <div key={index} className="mb-2">
                                        <div className="bg-light p-2 rounded">{msg}</div>
                                    </div>
                                ))}
                            </div>
                            <div className="input-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    value={message}
                                    onChange={(e) => setMessage(e.target.value)}
                                />
                                <button className="btn btn-primary" onClick={sendMessage}>
                                    Send
                                </button>
                            </div>
                        </>
                    ) : (
                        <p>Select a user to start chatting.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

// Helper function to decode JWT token
function parseJwt(token) {
    if (!token) return {};
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(c =>
        '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)
    ).join(''));
    return JSON.parse(jsonPayload);
}

// Helper function to generate Room ID based on user IDs
function generateRoomId(userId1, userId2) {
    return [userId1, userId2].sort().join('_');
}

export default Chat;
