import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

export default function Hashtag() {
  const { hashtag } = useParams(); // Get the hashtag from the URL
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPostsByHashtag = async () => {
      setError(null);
      try {
        const response = await fetch(`https://twit-backend-production.up.railway.app/api/posts/search?q=${encodeURIComponent(hashtag)}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `${localStorage.getItem('token')}`,
          }
        });

        if (!response.ok) {
          const errorText = await response.text(); // Capture the error message
          throw new Error(`Failed to fetch posts for hashtag: ${errorText}`);
        }

        const data = await response.json();
        setPosts(data.posts || []); // Default to empty array
      } catch (error) {
        setError(error.message || 'An unknown error occurred');
      }
    };

    fetchPostsByHashtag();
  }, [hashtag]);

  return (
    <div className="container-fluid bg-black p-4">
      <h1 className="text-white">Posts for #{hashtag}</h1>

      {error && <div className="alert alert-danger">Error: {error}</div>}
      {posts.length > 0 ? (
        <div className="row">
          {posts.map((post) => (
            <div key={post.id} className="col-md-4 mb-4">
              <div className="card bg-dark text-white">
                {post.image && (
                  <img src={post.image} className="card-img-top" alt={post.caption} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{post.caption}</h5>
                  <p className="card-text">Posted by {post.username}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-white">No posts found for this hashtag.</p>
      )}
    </div>
  );
}
