// UserSearch.js
import { useEffect, useState } from 'react';

const UserSearch = ({ onUserSelect, otherUserId, onBack }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [users, setUsers] = useState([]);
    const userToken = localStorage.getItem('token');

    useEffect(() => {
        const fetchUsers = async (query = '') => {
            try {
                const response = await fetch(`http://localhost:5000/api/posts/searchuser?q=${query}`, {
                    headers: { 'Authorization': ` ${localStorage.getItem("token")}` }
                });
                const data = await response.json();
                if (Array.isArray(data)) {
                    setUsers(data);
                } else {
                    console.error('Unexpected data format:', data);
                }
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers(searchQuery);
    }, [searchQuery]);

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    return (
        <div className={`col-md-4 ${otherUserId ? 'd-none d-md-block' : ''}`} style={{ height: "100vh", borderRight: '1px solid #ccc' }}>
            <h3>Search Users</h3>
            <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search users"
                    value={searchQuery}
                    onChange={handleSearchChange}
                />
            </div>
            <ul className="list-group">
                {Array.isArray(users) && users.map((user) => (
                    <li
                        key={user.id}
                        className="list-group-item d-flex justify-content-between align-items-center cursor-pointer"
                        onClick={() => onUserSelect(user.id)}
                    >
                        <img
                            src={`http://localhost:5000/public/${user.profilePic}`}
                            alt={`${user.name}'s profile`}
                            className="rounded-circle me-2"
                            style={{ width: '40px', height: '40px' }}
                        />
                        {user.name}
                    </li>
                ))}
            </ul>
            {/* Only show this button on small screens */}
            {otherUserId && (
                <div className="d-md-none mt-3">
                    <button
                        className="btn btn-primary"
                        onClick={onBack}
                    >
                        Back to Search
                    </button>
                </div>
            )}
        </div>
    );
};

export default UserSearch;
