import React, { useState , useNavigate } from 'react';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';

const Login = ({ show, handleClose }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const openModal = () => {
    navigate('/post', { state: { background: location } });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      localStorage.setItem('token', response.data.token);
      alert('Login successful');
      handleClose();
    } catch (err) {
      setError(err.response.data);
    }
  };

  return (
    <div className='bg-black'>
      <Modal
        show={show}
        onHide={handleClose}
        centered
        contentClassName="bg-black text-white"
        dialogClassName="modal-top"
      >
        <Modal.Header closeButton className="border-bottom border-secondary">
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            <div className="d-flex flex-column gap-3">
              <input
                type="email"
                className="form-control border-0 bg-secondary text-white"
                placeholder="Your Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="password"
                className="form-control border-0 bg-secondary text-white"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {/* Display error message in white color */}
            {error && <p style={{ color: "white" }}>{error}</p>}
            <Button variant="primary" type="submit" className="mt-3">Login</Button>
            
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Login;
