import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Tweets = () => {
  const [tweets, setTweets] = useState([]);
  const [users, setUsers] = useState({});
  const [likes, setLikes] = useState({}); // State to track likes
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch tweets
  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'GET',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Error fetching tweets: ${response.status}`);
      }

      const data = await response.json();
      setTweets(data);
      fetchUsers(data);
      fetchLikes(data); // Fetch likes for the tweets
    } catch (error) {
      console.error('Error fetching tweets:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Fetch users
  const fetchUsers = async (tweets) => {
    const userIds = [...new Set(tweets.map(tweet => tweet.userId))];
    userIds.forEach(async (userId) => {
      if (!users[userId]) {
        try {
          const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
            method: 'GET',
            headers: {
              'Authorization': `${localStorage.getItem('token')}`,
            },
          });

          if (userResponse.ok) {
            const userData = await userResponse.json();
            setUsers((prevUsers) => ({ ...prevUsers, [userId]: userData }));
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      }
    });
  };

  // Fetch likes
  const fetchLikes = async (tweets) => {
    try {
      const likeResponses = await Promise.all(tweets.map(tweet =>
        fetch(`http://localhost:5000/api/posts/${tweet.id}/likes`, {
          method: 'GET',
          headers: {
            'Authorization': `${localStorage.getItem('token')}`,
          },
        })
      ));
      
      const likeData = await Promise.all(likeResponses.map(response => response.json()));
      const likeCountMap = likeData.reduce((acc, { likeCount }, index) => {
        acc[tweets[index].id] = likeCount;
        return acc;
      }, {});
      
      setLikes(likeCountMap);
    } catch (error) {
      console.error('Error fetching like counts:', error);
    }
  };

  // Handle like/unlike
  const handleLike = async (tweetId) => {
    try {
      const response = await fetch('http://localhost:5000/api/posts/like', {
        method: 'POST',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: localStorage.getItem('userId'),
          postId: tweetId,
        }),
      });

      if (!response.ok) {
        throw new Error('Error liking the post');
      }

      setLikes((prevLikes) => ({
        ...prevLikes,
        [tweetId]: (prevLikes[tweetId] || 0) + 1,
      }));
    } catch (error) {
      console.error('Error liking the post:', error);
    }
  };

  const handleUnlike = async (tweetId) => {
    try {
      const response = await fetch('http://localhost:5000/api/posts/like', {
        method: 'DELETE',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: localStorage.getItem('userId'),
          postId: tweetId,
        }),
      });

      if (!response.ok) {
        throw new Error('Error unliking the post');
      }

      setLikes((prevLikes) => ({
        ...prevLikes,
        [tweetId]: (prevLikes[tweetId] || 0) - 1,
      }));
    } catch (error) {
      console.error('Error unliking the post:', error);
    }
  };

  useEffect(() => {
    fetchTweets();
  }, []);

  if (loading) {
    return <div className="container-fluid"><div className="alert alert-info">Loading...</div></div>;
  }

  return (
    <div className="container-fluid g-0 m-0 p-0 b-0 ">
      {error && <div className="alert alert-danger">Error: {error}</div>}
      <ul className="p-0">
        {tweets.map((tweet) => (
          <li key={tweet.id} className="border border-secondary bg-black text-white list-unstyled pb-3">
            <div className="media">
              <div className="media-body">
                {users[tweet.userId] && (
                  <span>
                    <h5 className="py-2 ps-3">
                      {users[tweet.userId].name}{' '}
                      <small className="text-secondary">@{users[tweet.userId].email}</small>
                      <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
                    </h5>
                  </span>
                )}
                <h5 className="ps-3 fw-light">{tweet.postText}</h5>
                {tweet.media && (
                  <img
                    src={`http://localhost:5000/public/${tweet.media}`}
                    className="ps-5 pe-5 rounded"
                    alt="Tweet Media"
                    style={{ width: '100%' }}
                  />
                )}
                <div className="ps-3 pt-2">
                  <button
                    className="btn btn-primary"
                    onClick={() => handleLike(tweet.id)}
                  >
                    Like
                  </button>
                  <button
                    className="btn btn-secondary ms-2"
                    onClick={() => handleUnlike(tweet.id)}
                  >
                    Unlike
                  </button>
                  <span className="ms-2">{likes[tweet.id] || 0} Likes</span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tweets;
