import React, { useState } from 'react';

export default function Thirdbar() {
  const [searchQuery, setSearchQuery] = useState('');
  const [users, setUsers] = useState([]);
  const [tweets, setTweets] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error state before making the request
    try {
      const response = await fetch(`http://localhost:5000/api/posts/search?q=${searchQuery}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Search results:', data); // Log data for debugging
        setUsers(data.users || []); // Set users to data.users if it exists
        setTweets(data.tweets || []); // Set tweets to data.tweets if it exists
      } else {
        const errorText = await response.text();
        console.error('Error submitting search query:', errorText);
        setError('An error occurred while fetching the search results.');
        setUsers([]); // Clear users on error
        setTweets([]); // Clear tweets on error
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
      setUsers([]); // Clear users on error
      setTweets([]); // Clear tweets on error
    }
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Search"
              className="input-group mx-2 my-4 rounded-pill p-2 bg-dark text-white border-primary"
              value={searchQuery}
              onChange={handleInputChange}
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-4">Search</button>
          </form>
          {error && <p className="text-danger">{error}</p>} {/* Display error message if there's an error */}
        </div>
        <div className="col-12">
          <h3>Search Results:</h3>
          <div className="results">
            {users.length > 0 && (
              <>
                <h4>Users:</h4>
                {users.map((user) => (
                  <div key={user.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5>{user.name}</h5>
                    <p className="text-secondary">@{user.email}</p>
                  </div>
                ))}
              </>
            )}
            {tweets.length > 0 && (
              <>
                <h4>Tweets:</h4>
                {tweets.map((tweet) => (
                  <div key={tweet.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5 className="fw-light">{tweet.postText}</h5>
                    {tweet.media && (
                      <img
                        src={`http://localhost:5000/public/${tweet.media}`}
                        className="img-fluid rounded mt-2"
                        alt="Tweet Media"
                      />
                    )}
                  </div>
                ))}
              </>
            )}
            {users.length === 0 && tweets.length === 0 && (
              <p>No results found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
