import React from 'react';
import { Link } from 'react-router-dom';

const Tweet = ({ tweet, user, isLiked, onToggleLike }) => {
  return (
    <li key={tweet.id} className="border border-secondary bg-black text-white list-unstyled pb-3">
      <div className="media">
        <div className="media-body">
          {user && (
            <span>
              <h5 className="py-2 ps-3">
                <Link to={`/profile/${user.id}`}> {user.name} </Link>
                <small className="text-secondary">@{user.email}</small>
                <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
              </h5>
            </span>
          )}
          <h5 className="ps-3 fw-light">{tweet.postText}</h5>
          {tweet.media && (
            <img
              src={tweet.media}
              className="ps-5 pe-5"
              alt="Tweet Media"
              style={{
                width: '100%',
                height: 'auto',
                borderRadius: '20px' // Rounded corners
              }}
            />
          )}
          <div className="ps-3 pt-2">
            <button
              className={`btn btn-lg ${isLiked ? 'text-danger' : 'text-light'}`}
              onClick={onToggleLike}
            >
              <i className={`bi ${isLiked ? 'bi-heart-fill text-danger' : 'bi-heart'}`}></i> {tweet.likesCount}
            </button>
          </div>
        </div>
      </div>
    </li>
  );
};

export default Tweet;
