import React, { useState, useEffect } from 'react';
import { Container, Form, Button, ListGroup, Alert } from 'react-bootstrap';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token'); // Get the token from local storage

  // Fetch comments for the post
  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`https://twit-backend-production.up.railway.app/api/posts/comments?twitterId=${postId}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setComments(data);
      } catch (error) {
        console.error('Error fetching comments:', error);
        setError('Failed to load comments');
      }
    };

    fetchComments();
  }, [postId, token]);

  // Handle new comment submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://twit-backend-production.up.railway.app/api/posts/comments', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: newComment, userId: 1, twitterId: postId }),
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      setNewComment('');
      // Fetch updated comments
      const updatedCommentsResponse = await fetch(`https://twit-backend-production.up.railway.app/api/posts/comments?twitterId=${postId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (!updatedCommentsResponse.ok) {
        throw new Error('Network response was not ok');
      }
      const updatedCommentsData = await updatedCommentsResponse.json();
      setComments(updatedCommentsData);
    } catch (error) {
      console.error('Error adding comment:', error);
      setError('Failed to add comment');
    }
  };

  return (
    <Container>
      <h3>Comments</h3>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Control
            as="textarea"
            rows={3}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Add a comment..."
            required
          />
        </Form.Group>
        <Button type="submit" variant="primary">Submit</Button>
      </Form>
      <ListGroup className="mt-3">
        {comments.length > 0 ? (
          comments.map((comment) => (
            <ListGroup.Item key={comment.id}>
              <strong>{comment.user.name}:</strong> {comment.content}
            </ListGroup.Item>
          ))
        ) : (
          <ListGroup.Item>No comments yet.</ListGroup.Item>
        )}
      </ListGroup>
    </Container>
  );
};

export default Comment;
