import React, { useState, useEffect } from 'react';
import './App.css';
import Sidebar from './Components/sidebar';
import Home from './Pages/Home';
import Explore from './Pages/explore';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate } from 'react-router-dom';
import Login from './Pages/login';
import SignupModal from './Pages/signup';
import ProtectedRoute from './Pages/protectedRoute';
import IndexPage from './Pages/IndexPage';
import CreatePostModal from './Components/Post';

function App() {
  const token = localStorage.getItem('token');
  const location = useLocation();
  const navigate = useNavigate();
  const state = location.state;
  const background = state && state.background;

  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showSignupModal, setShowSignupModal] = useState(false);

  const handleCloseLogin = () => {
    setShowLoginModal(false);
    navigate(-1); // Go back to the previous location
  };

  const handleShowLogin = () => setShowLoginModal(true);

  const handleCloseSignup = () => {
    setShowSignupModal(false);
    navigate(-1); // Go back to the previous location
  };

  const handleShowSignup = () => setShowSignupModal(true);

  useEffect(() => {
    if (location.pathname === '/login') {
      handleShowLogin();
    } else {
      setShowLoginModal(false);
    }
    if (location.pathname === '/signup') {
      handleShowSignup();
    } else {
      setShowSignupModal(false);
    }
  }, [location]);

  return (
    <>
      <div className="container-fluid g-0 h-100 ">
        <div className="row">
          {token && (
            <div className="col-auto col-md-3 col-xl-3 col-sm-2 px-0 bg-black">
              <Sidebar />
            </div>
          )}
          <div className={`col ${token ? 'col-md-6 col-xl-6 col-sm-10 px-0' : ''}`}>
            <Routes >
              {token ? (
                <>
                  <Route path="/home" element={<ProtectedRoute><Home /></ProtectedRoute>} />
                  <Route path="/explore" element={<ProtectedRoute><Explore /></ProtectedRoute>} />
                  <Route path="*" element={<Navigate to="/home" />} />
                </>
              ) : (
                <>
                  <Route path="/login" element={<Login show={showLoginModal} handleClose={handleCloseLogin} />} />
                  <Route path="/signup" element={<IndexPage />} />
                  <Route path="/" element={<IndexPage />} />
                  <Route path="*" element={<Navigate to="/login" />} />
                </>
              )}
            </Routes>
          </div>
        </div>
      </div>

      <SignupModal show={showSignupModal} handleClose={handleCloseSignup} />

      {/* Conditionally render the modal based on the current location */}
      {token && location.pathname === '/post' && <CreatePostModal />}
    </>
  );
}

export default App;
