import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

export default function Explore() {
  const location = useLocation();
  const initialSearchQuery = location.state?.searchQuery || '';

  const [searchQuery, setSearchQuery] = useState(initialSearchQuery);
  const [userFetch, setUserFetch] = useState([]); // Changed variable name
  const [error, setError] = useState(null);

  useEffect(() => {
    if (searchQuery) {
      fetchUserData(searchQuery); // Adjusted function name
    }
  }, [searchQuery]);

  const fetchUserData = async (query) => { // Adjusted function name
    setError(null);
    try {
      const response = await fetch(`http://localhost:5000/api/posts/search?q=${query}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUserFetch(data.users || []); // Changed variable name
      } else {
        const errorText = await response.text();
        console.error('Error submitting search query:', errorText);
        setError('An error occurred while fetching the search results.');
        setUserFetch([]); // Changed variable name
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
      setUserFetch([]); // Changed variable name
    }
  };

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    fetchUserData(searchQuery); // Adjusted function name
  };

  return (
    <div className="container g-0">
      <div className="row">
        <div className="col-12">
          <form onSubmit={handleSearchSubmit} className="d-flex">
            <input
              type="text"
              placeholder="Search"
              value={searchQuery}
              onChange={handleInputChange}
              className="px-4 py-2 my-2 rounded-pill border-primary bg-black text-white flex-grow-1"
            />
            <button type="submit" className="btn btn-primary rounded-pill mx-2 my-2 d-flex align-items-center">
              <i className="bi bi-search"></i>
            </button>
          </form>
          <h3>Search Results:</h3>
          <div className="results">
            {error && <p className="text-danger">{error}</p>}
            {userFetch.length > 0 ? ( // Changed variable name
              <>
                <h4>Users:</h4>
                {userFetch.map((user) => ( // Changed variable name
                  <div key={user.id} className="result-item border border-secondary bg-black text-white p-3 mb-3">
                    <h5>{user.name}</h5>
                    <p className="text-secondary">@{user.email}</p>
                  </div>
                ))}
              </>
            ) : (
              <p>No users found</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
