const handleSearchChange = (e) => {
  setSearchQuery(e.target.value);

  // Fetch users based on search input
  const fetchUsers = async () => {
      try {
          const response = await axios.get(`http://localhost:5000/api/posts/search?q=${e.target.value}`, {
              headers: { Authorization: `${localStorage.getItem('token')}` },
          });

          // Ensure the response is an array of users
          const data = response.data;
          setFilteredUsers(Array.isArray(data) ? data : []); // Ensure it's an array or empty
      } catch (error) {
          console.error('Error searching users:', error);
          setFilteredUsers([]); // Reset to an empty array in case of error
      }
  };

  fetchUsers();
};
