import React, { useState, useEffect } from 'react';
import { Container, Form, Button, ListGroup, Image } from 'react-bootstrap';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch(`https://twit-backend-production.up.railway.app/api/posts/comments/${postId}`, {
          headers: {
            'Authorization': `${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();

        // Fetch user details for each comment
        const commentsWithUserDetails = await Promise.all(
          data.map(async (comment) => {
            const userResponse = await fetch(`https://twit-backend-production.up.railway.app/api/users/${comment.userId}`, {
              headers: {
                'Authorization': `${token}`,
                'Content-Type': 'application/json'
              }
            });
            if (!userResponse.ok) {
              throw new Error('Network response was not ok');
            }
            const userData = await userResponse.json();
            return { ...comment, user: userData };
          })
        );

        setComments(commentsWithUserDetails);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    if (postId) {
      fetchComments();
    }
  }, [postId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://twit-backend-production.up.railway.app/api/posts/comments', {
        method: 'POST',
        headers: {
          'Authorization': `${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: newComment, userId, twitterId: postId }),
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      setNewComment('');

      // Fetch updated comments
      const updatedCommentsResponse = await fetch(`https://twit-backend-production.up.railway.app/api/posts/comments/${postId}`, {
        headers: {
          'Authorization': `${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (!updatedCommentsResponse.ok) {
        throw new Error('Network response was not ok');
      }
      const updatedCommentsData = await updatedCommentsResponse.json();

      // Fetch user details for the updated comments
      const updatedCommentsWithUserDetails = await Promise.all(
        updatedCommentsData.map(async (comment) => {
          const userResponse = await fetch(`https://twit-backend-production.up.railway.app/api/posts/users/${comment.userId}`, {
            headers: {
              'Authorization': `${token}`,
              'Content-Type': 'application/json'
            }
          });
          if (!userResponse.ok) {
            throw new Error('Network response was not ok');
          }
          const userData = await userResponse.json();
          return { ...comment, user: userData };
        })
      );

      setComments(updatedCommentsWithUserDetails);
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <Container>
      <h3>Comments</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Control
            as="textarea"
            rows={3}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Add a comment..."
            required
          />
        </Form.Group>
        <Button type="submit" variant="primary">Submit</Button>
      </Form>
      <ListGroup className="mt-3">
        {comments.map((comment) => (
          <ListGroup.Item key={comment.id}>
            <div className="d-flex align-items-center">
              <Image
                src={comment.user.profilePic}
                roundedCircle
                width={40}
                height={40}
                className="me-2"
              />
              <strong>{comment.user.name}</strong>
            </div>
            <p className="mb-0">{comment.content}</p>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default Comment;
