import React from 'react';
import './App.css';
import Sidebar from './Components/sidebar';
import CreatePostModal from './Components/Post';
import Home from './Pages/Home';
import Explore from './Pages/Explore';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

function App() {
  return (
    <Router>
      <div className="container-fluid">
        <div className="row">
          <div className="col-auto col-md-3 col-xl-3 px-sm-2 px-0 bg-black">
            <Sidebar />
          </div>
          <div className="col-md-9 col-xl-9 px-sm-10">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/explore" element={<Explore />} />
              {/* Add more Route components for other pages */}
            </Routes>
          </div>
        </div>
      </div>
      <CreatePostModal /> {/* Ensure CreatePostModal is outside the main layout */}
    </Router>
  );
}

export default App;
