import React, { useLocation, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Explore = () => {
  const location = useLocation();
  const searchState = location.state || { searchQuery: '', users: [], tweets: [] };
  
  const [users, setUsers] = useState(searchState.users);
  const [tweets, setTweets] = useState(searchState.tweets);
  const [searchQuery, setSearchQuery] = useState(searchState.searchQuery);

  return (
    <div className="container">
      <h3>Search Results for "{searchQuery}":</h3>
      <div className="results">
        {users.length > 0 && (
          <>
            <h4>Users:</h4>
            {users.map((user) => (
              <div key={user.id} className="result-item">
                <p>{user.name}</p>
                <p>{user.email}</p>
              </div>
            ))}
          </>
        )}
        {tweets.length > 0 && (
          <>
            <h4>Tweets:</h4>
            {tweets.map((tweet) => (
              <div key={tweet.id} className="result-item">
                <p>{tweet.postText}</p>
              </div>
            ))}
          </>
        )}
        {users.length === 0 && tweets.length === 0 && (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
};

export default Explore;
