import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useParams } from 'react-router-dom';

const Profile = () => {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserAndPosts = async () => {
      const token = localStorage.getItem('token');
      
      try {
        // Fetch user details
        const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
          method: 'GET',
          headers: {
            'Authorization': `${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!userResponse.ok) {
          throw new Error('Failed to fetch user details');
        }

        const userData = await userResponse.json();
        setUser(userData);

        // Fetch user's posts
        const postsResponse = await fetch(`http://localhost:5000/api/posts/${userId}`, {
          method: 'GET',
          headers: {
            'Authorization': `${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!postsResponse.ok) {
          throw new Error('Failed to fetch posts');
        }

        const postsData = await postsResponse.json();
        setPosts(postsData);

      } catch (err) {
        setError(err.message);
      }
    };

    fetchUserAndPosts();
  }, [userId]);

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  return (
    <Container className="mt-4">
      {user && (
        <Row>
          <Col md={12}>
          <Col md={12}>
          
            <Card className="border border-secondary bg-black text-white">
            <Card className="border border-secondary bg-black text-white">
              <img src=""alt="hjkhk" className='w-100 h-100' />
            </Card>
              <Card.Body>
                
                <Card.Title>{user.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-secondary">@{user.email}</Card.Subtitle>
                <Button variant="primary">Follow</Button>
              </Card.Body>
            </Card>
          </Col>
            {posts.length > 0 ? (
              <ul className="p-0">
                {posts.map(post => (
                  <li key={post.id} className="border border-secondary bg-black text-white list-unstyled pb-3">
                    <div className="media">
                      <div className="media-body">
                        <h5 className="py-2 ps-3">
                          {user.name} <small className="text-secondary">@{user.email}</small>
                          <small className="text-muted">{new Date(post.createdAt).toLocaleString()}</small>
                        </h5>
                        <p className="ps-3 fw-light">{post.postText}</p>
                        {post.media && (
                          <img
                            src={`http://localhost:5000/public/${post.media}`}
                            className="ps-5 pe-5 rounded"
                            alt="Post Media"
                            style={{ width: '100%' }}
                          />
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No posts found.</p>
            )}
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default Profile;
