import { useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5000');

const Chat = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [users, setUsers] = useState([]); // List of users
    const [filteredUsers, setFilteredUsers] = useState([]); // Filtered users based on search input
    const [searchQuery, setSearchQuery] = useState(''); // Search input state
    const [otherUserId, setOtherUserId] = useState(null);
    const userToken = localStorage.getItem('token'); // Get token from local storage
    const userId = userToken ? parseJwt(userToken).id : null; // Get user ID from token

    const roomId = otherUserId ? generateRoomId(userId, otherUserId) : null;

    useEffect(() => {
        if (!userId) return; // No user ID available

        // Fetch users from the API
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/posts/user', {
                    headers: { 'Authorization': `${localStorage.getItem("token")}` }
                });
                setUsers(response.data);
                setFilteredUsers(response.data); // Initialize filtered users
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();

        if (roomId) {
            // Join the room
            socket.emit('joinRoom', { roomId, userId });

            // Listen for messages
            socket.on('message', (message) => {
                setMessages((prevMessages) => [...prevMessages, message]);
            });

            return () => {
                socket.off('message');
            };
        }
    }, [roomId, userId, otherUserId]);

    const sendMessage = () => {
        if (message.trim() === '' || !roomId) return; // Don't send empty messages or if roomId is not available

        socket.emit('chatMessage', { roomId, message });
        setMessage(''); // Clear input field
    };

    // Handle search input
    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);

        // Filter users based on search input
        const filtered = users.filter(user =>
            user.name.toLowerCase().includes(e.target.value.toLowerCase())
        );
        setFilteredUsers(filtered);
    };

    return (
        <div>
            <div>
                <h3>Select a User to Chat</h3>
                <input
                    type="text"
                    placeholder="Search users..."
                    value={searchQuery}
                    onChange={handleSearchChange} // Handle search input
                />
                <ul>
                    {filteredUsers.map((user) => (
                        <li key={user.id} onClick={() => setOtherUserId(user.id)}>
                            {user.name}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h3>Chat</h3>
                <div>
                    {messages.map((msg, index) => (
                        <div key={index}>{msg}</div>
                    ))}
                </div>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
};

// Helper function to decode JWT token
function parseJwt(token) {
    if (!token) return {};
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
    return JSON.parse(jsonPayload);
}

function generateRoomId(userId1, userId2) {
    const sortedIds = [userId1, userId2].sort();
    return `${sortedIds[0]}_${sortedIds[1]}`;
}

export default Chat;
