import React, { useEffect, useState } from 'react';
import CreatePostModal from '../Components/Post'; // Adjust path as needed
import 'bootstrap/dist/css/bootstrap.min.css';
import CreatePostForm from '../Components/postForm';
const Tweets = () => {
  const [tweets, setTweets] = useState([]);
  const [users, setUsers] = useState({});

  const [loading, setLoading] = useState(true);
  const [showPostModal, setShowPostModal] = useState(true); // Always show modal

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'GET',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Error fetching tweets: ${response.status}`);
      }

      const data = await response.json();
      setTweets(data);
      fetchUsers(data);
    } catch (error) {
      console.error('Error fetching tweets:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  
  const fetchUsers = async (tweets) => {
    const userIds = [...new Set(tweets.map(tweet => tweet.userId))];
    userIds.forEach(async (userId) => {
      if (!users[userId]) {
        try {
          const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
            method: 'GET',
            headers: {
              'Authorization': `${localStorage.getItem('token')}`,
            },
          });

          if (userResponse.ok) {
            const userData = await userResponse.json();
            setUsers((prevUsers) => ({ ...prevUsers, [userId]: userData }));
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      }
    });
  };

  useEffect(() => {
    fetchTweets();
  }, []);
  const [data, setData] = useState({ postText: '', media: null });
  const [error, setError] = useState('');

  const extractHashtags = (text) => {
    const hashtagRegex = /#(\w+)/g;
    const hashtags = [];
    let match;
    while ((match = hashtagRegex.exec(text)) !== null) {
      hashtags.push(match[1]);
    }
    return hashtags;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError("No token found");
      return;
    }

    const formData = new FormData();
    formData.append('postText', data.postText);
    if (data.media) {
      formData.append('media', data.media);
    }

    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: "POST",
        headers: {
          "Authorization": `${token}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Error while creating post: ${response.status}`);
      }

      const text = extractHashtags(data.postText);
      if (text.length > 0) {
        const hashtagResponse = await fetch('http://localhost:5000/api/hashtag/', {
          method: "POST",
          headers: {
            "Authorization": `${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ text }),
        });

        if (!hashtagResponse.ok) {
          throw new Error(`Error while posting hashtags: ${hashtagResponse.status}`);
        }
      }

      setData({ postText: '', media: null });
      setError('');
      handleClose();
    } catch (error) {
      console.error("Error creating post:", error);
      setError(error.message || "An error occurred");
    }
  };
  if (loading) {
    return <div className="container-fluid"><div className="alert alert-info">Loading...</div></div>;
  }

  return (
    <>
      <header closeButton className="border-bottom border-secondary">
        <title>Create Post</title>
      </header>
      <body>
        <form onSubmit={handleSubmit}>
          <div className="d-flex">
            <img
              src="https://github.com/mdo.png"
              alt="User"
              className="rounded-circle me-3"
              width={50}
              height={50}
            />
            <textarea
              className="form-control border-0 bg-secondary text-white"
              rows="4"
              placeholder="What's happening?"
              style={{ resize: 'none' }}
              value={data.postText}
              onChange={(e) =>
                setData({ ...data, postText: e.target.value })
              }
              required
            ></textarea>
          </div>

          {error && <p style={{ color: "white" }}>{error}</p>}
          <footer className="d-flex justify-content-between border-top border-secondary">
            <div>
              <label htmlFor="media-upload" style={{ cursor: 'pointer', position: 'relative' }}>
                <i className="bi bi-image mx-2"></i>
                <input
                  id="media-upload"
                  type="file"
                  style={{
                    opacity: 0,
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    height: '100%',
                    width: '100%',
                    cursor: 'pointer',
                  }}
                  onChange={(e) =>
                    setData({ ...data, media: e.target.files[0] })
                  }
                />
              </label>
              <i className="bi bi-camera mx-2" style={{ cursor: 'pointer' }}></i>
              <i className="bi bi-bar-chart mx-2" style={{ cursor: 'pointer' }}></i>
              <i className="bi bi-emoji-smile mx-2" style={{ cursor: 'pointer' }}></i>
              <i className="bi bi-calendar mx-2" style={{ cursor: 'pointer' }}></i>
            </div>
            <button type="submit" variant="primary">
              Tweet
            </button>
          </footer>
        </form>
      </body>
      <div className="container-fluid g-0 m-0 p-0 b-0">
        {error && <div className="alert alert-danger">Error: {error}</div>}
        <ul className="p-0">
          {tweets.map((tweet) => (
            <li key={tweet.id} className="border border-secondary bg-black text-white list-unstyled pb-3">
              <div className="media">
                <div className="media-body">
                  {users[tweet.userId] && (
                    <span>
                      <h5 className="py-2 ps-3">
                        {users[tweet.userId].name}{' '}
                        <small className="text-secondary">@{users[tweet.userId].email}</small>
                        <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
                      </h5>
                    </span>
                  )}
                  <h5 className="ps-3 fw-light">{tweet.postText}</h5>
                  {tweet.media && (
                    <img
                      src={`http://localhost:5000/public/${tweet.media}`}
                      className="ps-5 pe-5 rounded"
                      alt="Tweet Media"
                      style={{ width: '100%' }}
                    />
                  )}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default Tweets;
