import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Tweets = () => {
  const [tweets, setTweets] = useState([]);
  const [users, setUsers] = useState({});
  const [likes, setLikes] = useState({});
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'GET',
        headers: {
          'Authorization': `${localStorage.getItem('token')}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Error fetching tweets: ${response.status}`);
      }

      const data = await response.json();
      setTweets(data);
      fetchUsers(data);
      fetchLikes(data);
    } catch (error) {
      console.error('Error fetching tweets:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchLikes = async (tweets) => {
    for (const tweet of tweets) {
      try {
        const response = await fetch(`http://localhost:5000/api/likes/post/${tweet.id}`, {
          method: 'GET',
          headers: {
            'Authorization': `${localStorage.getItem('token')}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setLikes((prevLikes) => ({ ...prevLikes, [tweet.id]: data.length }));
        }
      } catch (error) {
        console.error('Error fetching likes:', error);
      }
    }
  };

  const handleLike = async (tweetId) => {
    const userId = localStorage.getItem('userId');
    
    if (!userId) {
      setError('User not logged in');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/posts/likes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ postId: tweetId, userId }),
      });

      if (response.ok) {
        setLikes((prevLikes) => ({
          ...prevLikes,
          [tweetId]: (prevLikes[tweetId] || 0) + 1,
        }));
      }
    } catch (error) {
      console.error('Error liking the tweet:', error);
      setError('Failed to like tweet');
    }
  };

  const fetchUsers = async (tweets) => {
    const userIds = [...new Set(tweets.map(tweet => tweet.userId))];
    userIds.forEach(async (userId) => {
      if (!users[userId]) {
        try {
          const userResponse = await fetch(`http://localhost:5000/api/posts/user/${userId}`, {
            method: 'GET',
            headers: {
              'Authorization': `${localStorage.getItem('token')}`,
            },
          });

          if (userResponse.ok) {
            const userData = await userResponse.json();
            setUsers((prevUsers) => ({ ...prevUsers, [userId]: userData }));
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      }
    });
  };

  useEffect(() => {
    fetchTweets();
  }, []);

  if (loading) {
    return <div className="container-fluid"><div className="alert alert-info">Loading...</div></div>;
  }

  return (
    <div className="container-fluid g-0 m-0 p-0 b-0 ">
      {error && <div className="alert alert-danger">Error: {error}</div>}
      <ul className="p-0">
        {tweets.map((tweet) => (
          <li key={tweet.id} className="border border-secondary bg-black text-white list-unstyled pb-3">
            <div className="media">
              <div className="media-body">
                {users[tweet.userId] && (
                  <span>
                    <h5 className="py-2 ps-3">
                      {users[tweet.userId].name}{' '}
                      <small className="text-secondary">@{users[tweet.userId].email}</small>
                      <small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small>
                    </h5>
                  </span>
                )}
                <h5 className="ps-3 fw-light">{tweet.postText}</h5>
                {tweet.media && (
                  <img
                    src={`http://localhost:5000/public/${tweet.media}`}
                    className="ps-5 pe-5  rounded"
                    alt="Tweet Media"
                    style={{ width: '100%' }}
                  />
                )}
                <div className="ps-3">
                  <button className="btn btn-primary" onClick={() => handleLike(tweet.id)}>
                    Like {likes[tweet.id] || 0}
                  </button>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tweets;
