import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Form, Button, ListGroup } from 'react-bootstrap';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    // Fetch comments for the given postId
    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/posts/comments?twitterId=${postId}`);
        setComments(response.data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };
    fetchComments();
  }, [postId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/comments', { content: newComment, userId: 1, twitterId: postId });
      setNewComment('');
      // Fetch comments again to include the new one
      const response = await axios.get(`/api/comments?twitterId=${postId}`);
      setComments(response.data);
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <Container>
      <h3>Comments</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Control
            as="textarea"
            rows={3}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Add a comment..."
            required
          />
        </Form.Group>
        <Button type="submit" variant="primary">Submit</Button>
      </Form>
      <ListGroup className="mt-3">
        {comments.map((comment) => (
          <ListGroup.Item key={comment.id}>
            <strong>{comment.user.name}:</strong> {comment.content}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default Comment;
