import React, { useState, useEffect } from 'react';

export default function Tweets() {
  const [tweets, setTweets] = useState([]);
const token = localStorage.getItem("token")
  useEffect(() => {
    const fetchTweets = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/posts/', {  headers: {
          "authorization": `${token}`, 
        }});
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setTweets(data);
      } catch (error) {
        console.error("Error fetching tweets:", error);
      }
    };

    fetchTweets();
  }, []);

  return (
    <div className="container mt-4">
      {tweets.map(tweet => (
        <div key={tweet.id} className="card mb-3">
          <div className="card-body">
            {tweet.media && <img src={tweet.media} alt="Tweet media" className="card-img-top mb-3" />}
            <p className="card-text">{tweet.postText}</p>
            <p className="card-text"><small className="text-muted">{new Date(tweet.createdAt).toLocaleString()}</small></p>
          </div>
        </div>
      ))}
    </div>
  );
}
