import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate } from 'react-router-dom';
import Sidebar from './Components/sidebar';
import Home from './Pages/Home';
import Explore from './Pages/explore';
import Chat from './Pages/chat';
import Profile from './Pages/profile';
import IndexPage from './Pages/IndexPage';
import CreatePostModal from './Components/Post';
import Thirdbar from './Pages/thirdbar';
import EditModal from './Pages/Edit';
import { Modal, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const token = localStorage.getItem('token');
  const location = useLocation();
  const navigate = useNavigate();

  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showSignupModal, setShowSignupModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    switch (location.pathname) {
      case '/login':
        setShowLoginModal(true);
        break;
      case '/signup':
        setShowSignupModal(true);
        break;
      default:
        setShowLoginModal(false);
        setShowSignupModal(false);
        break;
    }
    if (location.pathname.includes('/edit')) {
      setShowEditModal(true);
    } else {
      setShowEditModal(false);
    }
  }, [location]);

  const handleCloseLogin = () => {
    setShowLoginModal(false);
    navigate(-1);
  };

  const handleCloseSignup = () => {
    setShowSignupModal(false);
    navigate(-1);
  };

  const handleCloseEdit = () => {
    setShowEditModal(false);
    navigate(-1);
  };

  return (
    <>
      <div className="container-fluid g-0 bg-black w-100" style={{ height: "100%" }}>
        <div className="row bg-black h-100">
          {/* Sidebar only on medium and larger screens */}
          {token && (
            <div className="col-md-3 col-sm-2 px-0 bg-black d-none d-md-block">
              <Sidebar />
            </div>
          )}

          <div className={`col ${token ? 'd-md-block' : 'd-none'} ${location.pathname === '/chat' ? '' : 'd-md-block'}`}>
            <Routes>
              {token ? (
                <>
                  <Route path="/home" element={<ProtectedRoute><Home /></ProtectedRoute>} />
                  <Route path="/explore" element={<ProtectedRoute><Explore /></ProtectedRoute>} />
                  <Route path="/chat" element={<ProtectedRoute><Chat /></ProtectedRoute>} />
                  <Route path="/profile/:userId" element={<ProtectedRoute><Profile /></ProtectedRoute>} />
                  <Route path="/edit/:userId" element={<ProtectedRoute><EditModal /></ProtectedRoute>} />
                  <Route path="*" element={<Navigate to="/home" />} />
                </>
              ) : (
                <>
                  <Route path="/login" element={<IndexPage />} />
                  <Route path="/signup" element={<IndexPage />} />
                  <Route path="/" element={<IndexPage />} />
                  <Route path="*" element={<Navigate to="/" />} />
                </>
              )}
            </Routes>
          </div>

          {token && location.pathname !== '/explore' && location.pathname !== '/chat' && (
            <div className="col-md-3 d-none d-md-block px-0 bg-black">
              <Thirdbar />
            </div>
          )}
        </div>
      </div>

      {/* Bootstrap Modals */}
      <Modal show={showLoginModal} onHide={handleCloseLogin}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Replace this with your Login form content */}
          <p>Login form goes here</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseLogin}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showSignupModal} onHide={handleCloseSignup}>
        <Modal.Header closeButton>
          <Modal.Title>Sign Up</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Replace this with your Signup form content */}
          <p>Signup form goes here</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseSignup}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showEditModal} onHide={handleCloseEdit}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Replace this with your Edit form content */}
          <p>Edit profile form goes here</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEdit}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      {token && location.pathname === '/post' && <CreatePostModal />}
    </>
  );
}

export default App;
