import React from 'react';
import './App.css';
import Sidebar from './Components/sidebar';
import CreatePostModal from './Components/Post';
import Home from './Pages/Home';
import Explore from './Pages/explore';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './Pages/login';
import Signup from './Pages/signup';
import ProtectedRoute from './Pages/';

function App() {
  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <div className="col-auto col-md-3 col-xl-3 col-sm-2 px-0 bg-black">
            <Sidebar />
          </div>
          <div className="col-auto col-md-6 col-xl-6 col-sm-10 px-0">
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/" element={
                <ProtectedRoute>
                  <Home />
                </ProtectedRoute>
              } />
              <Route path="/explore" element={
                <ProtectedRoute>
                  <Explore />
                </ProtectedRoute>
              } />
            </Routes>
          </div>
        </div>
      </div>
      <CreatePostModal /> {/* Ensure CreatePostModal is outside the main layout */}
    </>
  );
}

export default App;
