import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { Button, ListGroup, ListGroupItem, Form } from 'react-bootstrap';

const socket = io('http://localhost:5000');

const Chat = ({ currentUser }) => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [chatPartners, setChatPartners] = useState([]);

  useEffect(() => {
    // Fetch the list of users
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/posts/user',{

            
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setChatPartners(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  useEffect(() => {
    if (selectedUser) {
      socket.emit('joinRoom', { userId: currentUser.id, chatPartnerId: selectedUser.id });

      socket.on('message', (messageData) => {
        setMessages((prevMessages) => [...prevMessages, messageData]);
      });
    }

    return () => {
      if (selectedUser) {
        socket.off('message');
      }
    };
  }, [selectedUser]);

  const sendMessage = () => {
    if (messageInput.trim() && selectedUser) {
      socket.emit('message', {
        senderId: currentUser.id,
        receiverId: selectedUser.id,
        message: messageInput
      });
      setMessageInput('');
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-4">
          <ListGroup>
            {chatPartners.map((partner) => (
              <ListGroupItem
                key={partner.id}
                onClick={() => setSelectedUser(partner)}
                active={selectedUser?.id === partner.id}
              >
                {partner.name}
              </ListGroupItem>
            ))}
          </ListGroup>
        </div>
        <div className="col-md-8">
          {selectedUser && (
            <div>
              <h3>Chat with {selectedUser.name}</h3>
              <div className="chat-box">
                {messages
                  .filter((msg) =>
                    (msg.senderId === currentUser.id && msg.receiverId === selectedUser.id) ||
                    (msg.senderId === selectedUser.id && msg.receiverId === currentUser.id)
                  )
                  .map((msg) => (
                    <div key={msg.id} className={msg.senderId === currentUser.id ? 'message-sent' : 'message-received'}>
                      {msg.message}
                    </div>
                  ))}
              </div>
              <Form.Control
                type="text"
                value={messageInput}
                onChange={(e) => setMessageInput(e.target.value)}
                placeholder="Type your message..."
              />
              <Button onClick={sendMessage}>Send</Button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chat;
