import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function EditModal({ show, handleClose }) {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    dateOfBirth: '',
    profilePic: null,
    headerPic: null,
    bio: ''
  });
  const [error, setError] = useState('');
  const userId = localStorage.getItem('userId'); // Get user ID from local storage

  useEffect(() => {
    if (userId) {
      fetch(`https://twit-backend-production.up.railway.app/api/posts/user/${userId}`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` },
      })
        .then(response => response.json())
        .then(data => {
          setFormData({
            name: data.name || '',
            email: data.email || '',
            dateOfBirth: data.dateOfBirth || '',
            profilePic: '',
            headerPic: '',
            bio: data.bio || ''
          });
        })
        .catch(error => console.error('Error fetching user data:', error));
    }
  }, [userId]);

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.files[0]
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const form = new FormData();
    form.append('name', formData.name);
    form.append('email', formData.email);
    form.append('dateOfBirth', formData.dateOfBirth);
    form.append('bio', formData.bio);
    if (formData.profilePic) form.append('profilePic', formData.profilePic);
    if (formData.headerPic) form.append('headerPic', formData.headerPic);

    try {
      const response = await fetch(`https://twit-backend-production.up.railway.app/api/posts/user/edit/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: form
      });

      if (!response.ok) {
        throw new Error(`Error updating profile: ${response.status}`);
      }

      handleClose();
      navigate('/profile'); // Redirect to profile or other page after successful update
    } catch (error) {
      console.error('Error updating profile:', error);
      setError('An error occurred while updating the profile');
    }
  };

  return (
    <Modal
      show={show}
      onHide={handleClose}
      centered
      contentClassName="bg-black text-white"
    >
      <Modal.Header closeButton className="border-bottom border-secondary">
        <Modal.Title>Edit Profile</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {error && <p className="text-danger">{error}</p>}
          <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Date of Birth</Form.Label>
            <Form.Control
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Bio</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="bio"
              value={formData.bio}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Profile Picture</Form.Label>
            <Form.Control
              type="file"
              name="profilePic"
              onChange={handleFileChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Header Picture</Form.Label>
            <Form.Control
              type="file"
              name="headerPic"
              onChange={handleFileChange}
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Save Changes
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default EditModal;
